import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ItemService } from '../item.service';

@Component({
  selector: 'app-update-list',
  templateUrl: './update-list.component.html',
  styleUrls: ['./update-list.component.css'],
})
export class UpdateListComponent implements OnInit {
  item = {id:0,title: '', body: '' };

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private itemService: ItemService
  ) {}

  ngOnInit(): void {
    this.fetchItem();
  }

  fetchItem(): void {
    // const id = +this.route.snapshot.paramMap.get('id');
    // this.itemService.getItem(id).subscribe({
    //   next: (data) => {
    //     this.item = data;
    //   },
    //   error: (err) => {
    //     console.error('Error fetching item:', err);
    //     // Handle error (e.g., show a message to the user)
    //   }
    // });
  }

  updateItem(): void {
    this.itemService.updateItem(this.item.id, this.item).subscribe({
      next: () => {
        this.router.navigate(['/']);
      },
      error: (err) => {
        console.error('Error updating item:', err);
      }
    });
  }
  // ngOnInit(): void {
  //   this.route.paramMap.subscribe((param)=>{
  //     let id = Number(param.get('id'))
  //     this.updateItem(id)
  //   })
  // }

  // updateItem(id:number): void {
  //   this.itemService.updateItem(id,'').subscribe((data) => {
  //     this.item = data;
  //   })
  // }
}
<h1>Update Item</h1>
<form (ngSubmit)="updateItem()">
  <div>
    <label for="title">Title</label>
    <input type="text" id="title" name="title" required [(ngModel)]="item.title"/>
  </div>
  <div>
    <label for="body">Body</label>
    <textarea id="body" name="body" required [(ngModel)]="item.body"></textarea>
  </div>

  <button type="submit">Update</button>
</form>
